(function() {
  module.exports = {
    props: {
      "isOpened": {
        type: Boolean,
        "default": false
      }
    },
    data: function() {
      return {
        opened: this.isOpened
      };
    },
    methods: {
      setOpened: function() {
        this.opened = true;
        return this.isOpened = true;
      },
      setClosed: function() {
        this.opened = false;
        return this.isOpened = false;
      }
    },
    watch: {
      "isOpened": function(val) {
        if (val !== this.opened) {
          return this.toggle();
        }
      }
    }
  };

}).call(this);
